# calculate hamming distance between two strings

str1 = ""
str2 = ""

def hamming_distance(str1, str2):
    # Count differences between corresponding characters
    count = 0
    for c1, c2 in zip(str1, str2):
        if c1 != c2:
            count += 1
            
    return count

print(hamming_distance(str1, str2))

